import React, { useState } from 'react';
import './Resume.css';

const Resume = () => {
  const [questions, setQuestions] = useState([]);
  const [newQuestion, setNewQuestion] = useState("");

  const addQuestion = () => {
    if (newQuestion.trim() !== "") {
      setQuestions([...questions, { question: newQuestion, answer: "", isOpen: false }]);
      setNewQuestion("");
    }
  };

  const toggleQuestion = (index) => {
    setQuestions(questions.map((q, i) => i === index ? { ...q, isOpen: !q.isOpen } : q));
  };

  const handleAnswerChange = (index, answer) => {
    setQuestions(questions.map((q, i) => i === index ? { ...q, answer } : q));
  };

  return (
    <div className="resume-container">
      <h1>자기소개서</h1>
      <div className="add-question">
        <input
          type="text"
          value={newQuestion}
          onChange={(e) => setNewQuestion(e.target.value)}
          placeholder="새 문항을 입력하세요"
        />
        <button onClick={addQuestion}>문항 추가</button>
      </div>
      <div className="questions-list">
        {questions.map((q, index) => (
          <div key={index} className="question-item">
            <div className="question-header" onClick={() => toggleQuestion(index)}>
              <h3>{q.question}</h3>
              <button>{q.isOpen ? "접기" : "열기"}</button>
            </div>
            {q.isOpen && (
              <textarea
                value={q.answer}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
                placeholder="답변을 작성하세요"
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Resume;
